    @Test
    public void testGetHolidays()
    {
        TianApiService service = new TianApiServiceImpl();
        GetHolidaysReq req = new GetHolidaysReq();
        req.setDate("2021-01-01");
        req.setType(TianApiHolidayConstants.TYPE_YEAR);
        req.setMode(TianApiHolidayConstants.MODE_ALL);
        req.setKey("");
        System.out.println(service.getHolidays(req));
    }

    @Test
    public void testGetCalendar()
    {
        LocalDate cj = new LocalDate("2021-02-11");
        LunarCalendar lunarCalendar = LunarCalendar.solar2Lunar(cj.toDateTimeAtCurrentTime().toGregorianCalendar());
        System.out.println(lunarCalendar);
        System.out.println(lunarCalendar.getLunar(false));
        System.out.println(lunarCalendar.getTime() + " <====> " + lunarCalendar.getFullLunarName());
        System.out.println((new TianXingHolidayCalendar("")).generateCalendar());
    }

